import logo from './logo.svg';
import './App.css';
import React, {useState} from 'react';
import Product from './Product';

function App() {

  const [products, setProducts] = useState([
    {id: 1, name: "boot jeans", color: "blue"},
    {id: 2, name: "wide jeans", color: "blue"},
    {id: 3, name: "straight jeans", color: "white"}
  ]);

  const addBlue = () => {
    setProducts(products.filter(product => product.color == "blue"))
  };

  return (
    <div className="App">
      <h1>Zara</h1>
      <div className="product-grid">
        {products.map(product => (
          <Product 
            key={product.id}
            name={product.name}
            color={product.color}
          />
        ))}
      </div>
      <button onClick={addBlue}>Remove blue products</button>
    </div>
  );
}

export default App;
