import React, { useState, useEffect } from 'react';
import Product from './Product';

function App() {
  const [products, setProducts] = useState([
    { id: 1, name: "boot jeans", color: "blue", image: "https://static.zara.net/assets/public/159b/e920/f5354f12aebb/482e85a52763/08246067250-e1/08246067250-e1.jpg?ts=1712135152369&w=563" },
    { id: 2, name: "wide jeans", color: "red", image: "https://static.zara.net/assets/public/0a84/f835/7ec7417a8dc3/612916f34de6/06045022800-015-e1/06045022800-015-e1.jpg?ts=1710241554433&w=563" },
    { id: 3, name: "straight jeans", color: "white", image: "https://static.zara.net/photos///2023/I/0/1/p/1934/246/800/2/w/563/1934246800_6_1_1.jpg?ts=1691144043427" }
  ]);

  const [selectedColors, setSelectedColors] = useState([]);

  const addColor = (color) => {
    setSelectedColors(prevColors => {
      // If the color is already selected, remove it, otherwise add it
      if (prevColors.includes(color)) {
        return prevColors.filter(c => c !== color);
      } else {
        return [...prevColors, color];
      }
    });
  };

  const filteredProducts = products.filter(product => 
    selectedColors.length === 0 || selectedColors.includes(product.color)
  );

  return (
    <div className="App">
      <h1>Zara</h1>
      <div className="product-grid">
        {filteredProducts.map(product => (
          <Product
            key={product.id}
            name={product.name}
            color={product.color}
            image={product.image}
          />
        ))}
      </div>
      <button onClick={() => addColor("blue")}>Toggle blue</button>
      <button onClick={() => addColor("white")}>Toggle white</button>
    </div>
  );
}

export default App;
