    const {id} = useParams();
    const product = database.find(p => p.id == parseInt(id, 10));

    if (!product) {
        return <div>Product not found</div>;
    }

    return (
        <div className='screen'>

            <div>

            </div>

            <div>

            </div>

        </div>
    );

}

export default ProductInfo;
